// src/components/TicketForm.js
import React, { useState } from 'react';

function TicketForm() {
  const [form, setForm] = useState({
    event_id: '',
    name: '',
    email: '',
    quantity: 1
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost/tickettouch/public/api/tickets', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    })
      .then(res => res.json())
      .then(() => {
        alert('üéâ ƒê·∫∑t v√© th√†nh c√¥ng!');
        setForm({ event_id: '', name: '', email: '', quantity: 1 });
      });
  };

  return (
    <form onSubmit={handleSubmit} className="ticket-form">
      <h2>üìù ƒê·∫∑t v√©</h2>
      <input name="event_id" placeholder="ID s·ª± ki·ªán" value={form.event_id} onChange={handleChange} required />
      <input name="name" placeholder="H·ªç t√™n" value={form.name} onChange={handleChange} required />
      <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
      <input name="quantity" type="number" min="1" value={form.quantity} onChange={handleChange} required />
      <button type="submit">ƒê·∫∑t v√©</button>
    </form>
  );
}

export default TicketForm;
