import React, { useState } from 'react';

function TicketForm() {
  const [form, setForm] = useState({
    event_id: '',
    name: '',
    email: '',
    quantity: 1
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost/tickettouch/public/api/tickets', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    })
      .then(res => res.json())
      .then(() => {
        alert('üéâ ƒê·∫∑t v√© th√†nh c√¥ng!');
        setForm({ event_id: '', name: '', email: '', quantity: 1 });
      });
  };

  return (
    <div className="card shadow p-4">
      <h4 className="mb-3">üìù ƒê·∫∑t v√©</h4>
      <form onSubmit={handleSubmit}>
        <input name="event_id" className="form-control mb-3" placeholder="ID s·ª± ki·ªán" value={form.event_id} onChange={handleChange} required />
        <input name="name" className="form-control mb-3" placeholder="H·ªç t√™n" value={form.name} onChange={handleChange} required />
        <input name="email" type="email" className="form-control mb-3" placeholder="Email" value={form.email} onChange={handleChange} required />
        <input name="quantity" type="number" min="1" className="form-control mb-3" value={form.quantity} onChange={handleChange} required />
        <button type="submit" className="btn btn-success w-100">ƒê·∫∑t v√©</button>
      </form>
    </div>
  );
}

export default TicketForm;
