import React, { useState } from 'react';

function LoginForm({ onLoginSuccess }) {
  const [form, setForm] = useState({ email: '', password: '' });
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setUser(null);

    try {
      const loginRes = await fetch('http://localhost:8000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(form)
      });

      if (!loginRes.ok) {
        throw new Error('Sai thông tin đăng nhập');
      }

      const loginData = await loginRes.json();
      console.log('✅ Đăng nhập thành công:', loginData);

      // Gọi /me để lấy thông tin người dùng
      const userRes = await fetch('http://localhost:8000/me', {
        method: 'GET',
        credentials: 'include'
      });

      if (!userRes.ok) {
        throw new Error('Không lấy được thông tin người dùng');
      }

      const userData = await userRes.json();
      console.log('👤 Người dùng:', userData);
      setUser(userData);

      if (onLoginSuccess) onLoginSuccess(userData);
    } catch (err) {
      console.error('❌ Lỗi:', err.message);
      setError(err.message);
    }
  };

  const handleLogout = async () => {
    try {
      const logoutRes = await fetch('http://localhost:8000/logout', {
        method: 'POST',
        credentials: 'include'
      });

      if (!logoutRes.ok) {
        throw new Error('Lỗi khi đăng xuất');
      }

      const logoutData = await logoutRes.json();
      console.log('🚪 Đã đăng xuất:', logoutData);
      setUser(null);
    } catch (err) {
      console.error('❌ Lỗi đăng xuất:', err.message);
    }
  };

  return (
    <div className="card shadow p-4 mx-auto" style={{ maxWidth: '500px' }}>
      <h4 className="mb-3">🔐 Đăng nhập Laravel</h4>

      {user ? (
        <div>
          <p>👋 Xin chào, <strong>{user.name}</strong>!</p>
          <p>Email: {user.email}</p>
          <button className="btn btn-danger w-100" onClick={handleLogout}>Đăng xuất</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            className="form-control mb-3"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            className="form-control mb-3"
            placeholder="Mật khẩu"
            value={form.password}
            onChange={handleChange}
            required
          />
          <button type="submit" className="btn btn-primary w-100">Đăng nhập</button>
        </form>
      )}

      {error && <div className="alert alert-danger mt-3">{error}</div>}
    </div>
  );
}

export default LoginForm;
