import React, { useState } from 'react';

function LoginForm({ onLoginSuccess }) {
  const [form, setForm] = useState({ email: '', password: '' });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost/tickettouch/public/api/login', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    })
      .then(res => res.json())
      .then(data => {
        if (data.token) {
          alert('üéâ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!');
          onLoginSuccess();
        } else {
          alert('‚ùå Sai th√¥ng tin ƒëƒÉng nh·∫≠p');
        }
      });
  };

  return (
    <div className="card shadow p-4 mx-auto" style={{ maxWidth: '500px' }}>
      <h4 className="mb-3">üîê ƒêƒÉng nh·∫≠p</h4>
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" className="form-control mb-3" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" className="form-control mb-3" placeholder="M·∫≠t kh·∫©u" onChange={handleChange} required />
        <button type="submit" className="btn btn-primary w-100">ƒêƒÉng nh·∫≠p</button>
      </form>
    </div>
  );
}

export default LoginForm;
