import React, { useState } from 'react';

function LoginForm({ onLoginSuccess }) {
  const [form, setForm] = useState({ email: '', password: '' });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost:8000/login', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  credentials: 'include', // quan tr·ªçng ƒë·ªÉ g·ª≠i cookie
  body: JSON.stringify({
    email: 'admin@example.com',
    password: 'password'
  })
})
  .then(res => res.json())
  .then(data => {
    console.log('‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng:', data);
  })
  .catch(err => {
    console.error('‚ùå L·ªói ƒëƒÉng nh·∫≠p:', err);
  });
  fetch('http://localhost:8000/me', {
  method: 'GET',
  credentials: 'include'
})
  .then(res => res.json())
  .then(data => {
    console.log('üë§ Ng∆∞·ªùi d√πng:', data);
  });
  fetch('http://localhost:8000/logout', {
  method: 'POST',
  credentials: 'include'
})
  .then(res => res.json())
  .then(data => {
    console.log('üö™ ƒê√£ ƒëƒÉng xu·∫•t:', data);
  });
  };

  return (
    <div className="card shadow p-4 mx-auto" style={{ maxWidth: '500px' }}>
      <h4 className="mb-3">üîê ƒêƒÉng nh·∫≠p</h4>
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" className="form-control mb-3" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" className="form-control mb-3" placeholder="M·∫≠t kh·∫©u" onChange={handleChange} required />
        <button type="submit" className="btn btn-primary w-100">ƒêƒÉng nh·∫≠p</button>
      </form>
    </div>
  );
}

export default LoginForm;
